INSERT INTO "moves" VALUES(491,'Sleep Powder',11,15,0,75,0,0,3,'0000',0,0,'Puts the target to sleep.');
INSERT INTO "moves" VALUES(492,'Sleep Talk',0,10,0,255,0,0,3,'0000',0,0,'Does nothing if the user is awake. If the user asleep, it randomly performs one of its attacks. Rest will fail if it is selected. Sleep Talk''s generated attacks do not cost PP, but it cannot generate moves with 0 PP, itself, Assist, Bide, Chatter, Copycat, Disabled attacks, Focus Punch, Me First, Metronome, Mirror Move, Uproar, or attacks with a charge-up turn like Fly or Skull Bash. (Moves with a recharge turn like Hyper Beam can be generated.)');
INSERT INTO "moves" VALUES(493,'Sludge',3,20,65,100,0,0,2,'0000',0,0,'Has a 30% chance to poison the target.');
INSERT INTO "moves" VALUES(494,'Sludge Bomb',3,10,90,100,0,0,2,'0000',0,0,'Has a 30% chance to poison the target.');
