INSERT INTO "moves" VALUES(496,'Smack Down',5,15,50,100,0,0,1,'0000',0,0,'Until the target switches out, it loses its immunity to Ground-type moves if it is a Flying-type or has Levitate, unless it has used Roost on the same turn. The effects of Magnet Rise and Telekinesis are also removed, and this move can hit opponents in the middle of using Sky Drop, Fly or Bounce, immediately canceling the latter two.');
INSERT INTO "moves" VALUES(497,'Smelling Salts',0,10,70,100,0,0,1,'0000',0,0,'If the target is paralyzed, power is doubled but the target will be cured.');
