INSERT INTO "moves" VALUES(505,'Solar Beam',11,10,120,100,0,0,2,'0000',0,0,'The user prepares on turn one, becoming uncontrollable, and then attacks on turn two. During Sunny Day, this move fires immediately; during Rain Dance, Sandstorm and Hail, this move has half power.');
