INSERT INTO "moves" VALUES(514,'Spite',7,10,0,100,0,0,3,'0000',0,0,'The target''s most recent move is reduced by 4 PP; this fails if the target has not yet performed a move, if the target''s last move has run out of PP, or if the target can only use Struggle.');
INSERT INTO "moves" VALUES(515,'Splash',0,40,0,255,0,0,3,'0000',0,0,'Doesn''t do anything (but we still love it). Unfortunately, it also cannot be used during the effects of Gravity! :(');
INSERT INTO "moves" VALUES(516,'Spore',11,15,0,100,0,0,3,'0000',0,0,'Puts the target to sleep.');
INSERT INTO "moves" VALUES(517,'Stealth Rock',5,20,0,255,0,0,3,'0000',0,0,'Damages opponents every time they switch in until they use Rapid Spin. Saps a fraction of max HP determined by the effectiveness of Rock-type attacks against the opponent''s type: 1/32 for 1/4x, 1/16 for 1/2x, 1/8 for 1x, 1/4 for 2x and 1/2 for 4x. For example, Stealth Rock saps 50% of an Ice/Flying Pokemon''s max HP when it switches in.');
INSERT INTO "moves" VALUES(518,'Steam Eruption',10,5,110,95,0,0,2,'0000',0,0,'The user immerses the target in superheated steam. This may also leave the target with a burn.');
INSERT INTO "moves" VALUES(519,'Steamroller',6,20,65,100,0,0,1,'0000',0,0,'Has a 30% chance to make the target flinch; also retains its normal accuracy and gains doubled power against Minimized Pokemon.');
INSERT INTO "moves" VALUES(520,'Steel Wing',8,25,70,90,0,0,1,'0000',0,0,'Has a 10% chance to raise the user''s Defense by 1 stage.');
INSERT INTO "moves" VALUES(521,'Sticky Web',6,20,0,255,0,0,3,'0000',0,0,'The user weaves a sticky net around the opposing team, which lowers their Speed stat upon switching into battle.');
INSERT INTO "moves" VALUES(522,'Stockpile',0,20,0,255,0,0,3,'0000',0,0,'Can be used up to three times to power Spit Up or Swallow. Also raises the user''s Defense and Special Defense by 1 stage each. Stockpiles and the defensive boosts are lost when these Swallow or Spit Up is used or the user switches out. However, the boosts can be Baton Passed.');
