INSERT INTO "moves" VALUES(528,'String Shot',6,40,0,95,0,0,3,'0000',0,0,'Lowers the target''s Speed by 1 stage.');
INSERT INTO "moves" VALUES(529,'Struggle',0,1,50,255,0,0,1,'0000',0,0,'Used automatically when all of the user''s other moves have run out of PP or are otherwise inaccessible. The user receives recoil damage equal to 1/4 of its max HP. Struggle is classified as a typeless move and will hit any Pokemon, including Rock-, Steel- and Ghost-type Pokemon, for normal damage.');
INSERT INTO "moves" VALUES(530,'Struggle Bug',6,20,50,100,0,0,2,'0000',0,0,'Lowers the target''s Special Attack by 1 stage.');
INSERT INTO "moves" VALUES(531,'Stun Spore',11,30,0,75,0,0,3,'0000',0,0,'Paralyzes the target.');
INSERT INTO "moves" VALUES(532,'Submission',1,25,80,80,0,0,1,'0000',0,0,'The user receives 1/4 recoil damage.');
INSERT INTO "moves" VALUES(533,'Substitute',0,10,0,255,0,0,3,'0000',0,0,'The user takes one-fourth of its maximum HP to create a substitute; this move fails if the user does not have enough HP for this. Until the substitute is broken, it receives damage from all attacks made by other Pokemon and shields the user from status effects and stat modifiers caused by other Pokemon. The user is still affected by Tickle, Hail, Sandstorm and Attract from behind its Substitute. If a Substitute breaks from a hit during a multistrike move such as Fury Attack, the user takes damage from the remaining strikes.');
INSERT INTO "moves" VALUES(534,'Sucker Punch',16,5,80,100,0,0,1,'0000',0,0,'Almost always goes first, but fails if the target doesn''t select a move that will damage the user or its allies. The move also fails if the target uses an attack with higher priority or if the target is faster and uses an attack with the same priority.');
INSERT INTO "moves" VALUES(535,'Sunny Day',9,5,0,255,0,0,3,'0000',0,0,'Cancels all other weather moves. For 5 turns: freezing is prevented, the power of Fire attacks is increased by 50%, the power of Water attacks is decreased by 50%, Solarbeam fires immediately, Thunder becomes 50% accurate, and the healing power of Morning Sun, Synthesis and Moonlight is increased from 1/2 to 2/3 of the user''s max HP. The effects of Sunny Day will last for eight turns if its user is holding Heat Rock.');
INSERT INTO "moves" VALUES(536,'Super Fang',0,10,0,90,0,0,1,'0000',0,0,'This move halves the target''s current HP.');
