INSERT INTO "moves" VALUES(555,'Taunt',16,20,0,100,0,0,3,'0000',0,0,'Prevents the target from using non-damaging moves for three turns. Assist, Copycat, Me First, Metronome, Mirror Move and Sleep Talk are prevented during this time, but Bide, Counter, Endeavor, Metal Burst and Mirror Coat are not prevented.');
INSERT INTO "moves" VALUES(556,'Techno Blast',0,5,85,100,0,0,2,'0000',0,0,'This move''s type changes according to the user''s held Drives. Note that there are only Drives corresponding to four types: Electric (Shock), Fire (Burn), Ice (Chill) and Water (Douse).');
INSERT INTO "moves" VALUES(557,'Teeter Dance',0,20,0,100,0,0,3,'0000',0,0,'Each active Pokemon, except the user, becomes confused.');
INSERT INTO "moves" VALUES(558,'Telekinesis',13,15,0,255,0,0,3,'0000',0,0,'For three turns, the target becomes immune to Ground-type moves, entry hazards Spikes and Toxic Spikes, and the Arena Trap ability, but all other moves except for one-hit KO moves have perfect accuracy against the target. The moves Gravity or Ingrain and the hold item Iron Ball negate this effect and prevent this move from succeeding.');
INSERT INTO "moves" VALUES(559,'Teleport',13,20,0,255,0,0,3,'0000',0,0,'Escapes from wild battles; fails automatically in trainer and link battles.');
INSERT INTO "moves" VALUES(560,'Thief',16,10,60,100,0,0,1,'0000',0,0,'Steals the target''s held item unless the user is already holding an item or the target has Sticky Hold or Multitype. A stolen item cannot be recovered by using Recycle.');
INSERT INTO "moves" VALUES(561,'Thousand Arrows',4,10,90,100,0,0,1,'0000',0,0,'This move also hits opposing Pok√©mon that are in the air. Those Pok√©mon are knocked down to the ground.');
