INSERT INTO "moves" VALUES(564,'Thunder',12,10,110,70,0,0,2,'0000',0,0,'Has a 30% chance to paralyze the target. It also has normal accuracy against mid-air Pokemon who have used Fly, Bounce or Sky Drop. During Sunny Day, this move has 50% accuracy. During Rain Dance, this move will never miss under normal circumstances.');
INSERT INTO "moves" VALUES(565,'Thunder Fang',12,15,65,95,0,0,1,'0000',0,0,'Has a 10% chance to paralyze the target. Has 10% chance to make the target flinch. Both effects can occur from a single use.');
