INSERT INTO "items" VALUES(14,"Gooey Mulch",0,200,0,0,0,0,0,"00","A plant fertilizer spread on soft soil. It ups the number of times new plants grow where mature plants withered.","./");
INSERT INTO "items" VALUES(15,"Green Shard",0,200,0,0,0,0,0,"00","A small green shard. It appears to be from some sort of implement made long ago.","./");
INSERT INTO "items" VALUES(16,"Growth Mulch",0,200,0,0,0,0,0,"00","A plant fertilizer spread on soft soil. It speeds up the growth of Berries. However, it also dries the soil faster.","./");
INSERT INTO "items" VALUES(17,"Heart Scale",0,100,0,0,0,0,0,"00","A pretty, heart-shaped scale that is extremely rare. It glows faintly in the colors of the rainbow.","./");
INSERT INTO "items" VALUES(18,"Helix Fossil",0,1000,0,0,0,0,0,"00","A fossil of an ancient Pok√©mon that lived in the sea. It appears to be part of a seashell.","./");
INSERT INTO "items" VALUES(19,"Honey",0,100,0,0,0,0,0,"00","A sweet honey with a lush aroma that attracts wild Pok√©mon when it is used in grass, caves, or on special trees.","./");
INSERT INTO "items" VALUES(20,"Leaf Stone",0,2100,0,0,0,0,0,"00","A peculiar stone that makes certain species of Pok√©mon evolve. It has a leaf pattern.","./");
INSERT INTO "items" VALUES(21,"Lustrous Orb",0,10000,0,0,0,0,0,"00","A beautifully glowing orb to be held by PALKIA. It boosts the power of Dragon- and Water-type moves.","./");
INSERT INTO "items" VALUES(22,"Max Repel",0,700,0,0,0,0,0,"00","An item that prevents weak wild Pok√©mon from appearing for 250 steps after its use.","./");
INSERT INTO "items" VALUES(23,"Moon Stone",0,2100,0,0,0,0,0,"00","A peculiar stone that makes certain species of Pok√©mon evolve. It is as black as the night sky.","./");
INSERT INTO "items" VALUES(24,"Nugget",0,10000,0,0,0,0,0,"00","A nugget of pure gold that gives off a lustrous gleam. It can be sold at a high price to shops.","./");
INSERT INTO "items" VALUES(25,"Odd Keystone",0,2100,0,0,0,0,0,"00","A vital item that is needed to keep a stone tower from collapsing. Voices can be heard from it occasionally.","./");
INSERT INTO "items" VALUES(26,"Old Amber",0,1000,0,0,0,0,0,"00","A piece of amber that contains the genetic material of an ancient Pok√©mon. It is clear with a reddish tint.","./");
